	public static MailService get() {
		return Simple.get().getInjector().getProvider((MailService.class)).get();
	}

	public void sendWelcomeEmail(String email, User u) throws MessagingException {

		IRequestCycle cycle = RequestCycle.get();
		if (cycle == null || cycle.getRequest() == null)
			throw new MessagingException("No Request Cycle, so what website?");
		if (!this.email.matcher(email).matches()) {
			throw new IllegalArgumentException("Can only send to emails");
		}

		Properties properties = Simple.get().getProperties();
		if (!(properties.containsKey("mail.smtp.host")
				&& properties.containsKey("mail.smtp.port")
				&& properties.containsKey("mail.smtp.user")
				&& properties.containsKey("mail.smtp.password"))) {
			System.out.println("Main not configured, set the following properties in your settings.properties file." + "mail.smtp.port\nmail.smtp.auth\nmail.smtp.host\nmail.smtp.username\nmail.smtp.password\n" + "");
			return;
		}

		String to = email;
		String from = "SignupNotification@" + Simple.getBaseDomain();

		final String user = properties.getProperty("mail.smtp.user");
		final String password = properties.getProperty("mail.smtp.password");
		properties.put("mail.smtp.auth", "true");
		Session session = Session.getInstance(properties,
				new javax.mail.Authenticator() {
					protected PasswordAuthentication getPasswordAuthentication() {
						return new PasswordAuthentication(user, password);
					}
				});

		MimeMessage message = new MimeMessage(session);

		message.setFrom(new InternetAddress(from));
		message.addRecipient(javax.mail.Message.RecipientType.TO,
				new InternetAddress(to));
		Website current = Website.getCurrent();
		if (current != null) {
			message.setSubject("Signup Notification @ " + current.getProduction());
		} else {
			message.setSubject("Signup Notification @ " + Simple.getBaseDomain());

		}
		BodyPart messageBodyPart = new MimeBodyPart();

		messageBodyPart.setText(
				"An account has been created @ " + Simple.getBaseDomain()
						+ "\nUsername: " + u.getIdentifier()
						+ "\nAdmin Link: " + Simple.getBaseDomain() + "?user=" + u.getIdentifier() + "\n" + "\n" + "This account may have been auto-generated. To set a password, follow this link." + "Follow this link to access your account and update your password in the User Settings. This direct link will self-destruct once used. \n http://admin." + Simple.getBaseDomain() + ":" + Simple.getPort() + "?nonce=" + SecurityService.get().generateNonce(u));

		Multipart multipart = new MimeMultipart();

		// Set text message part
		multipart.addBodyPart(messageBodyPart);

		// Send the complete message parts
		message.setContent(multipart);

		// Send message
		Transport.send(message);
	}

	public void sendNonceEmail(User u) throws MessagingException {
		String email = u.getContactInfo().getEmail();
		IRequestCycle cycle = RequestCycle.get();
		if (cycle == null || cycle.getRequest() == null)
			throw new MessagingException("No Request Cycle, so what website?");
		if (!this.email.matcher(email).matches()) {
			throw new IllegalArgumentException("Can only send to emails");
		}

		Properties properties = Simple.get().getProperties();
		if (!(properties.containsKey("mail.smtp.host")
				&& properties.containsKey("mail.smtp.port")
				&& properties.containsKey("mail.smtp.user")
				&& properties.containsKey("mail.smtp.password"))) {
			System.out.println("Main not configured, set the following environment variables." + "mail.smtp.port\nmail.smtp.auth\nmail.smtp.host\nmail.smtp.username\nmail.smtp.password\n" + "");
			return;
		}

		String to = email;
		String from = "AccessControl@" + Simple.getBaseDomain();

		final String user = properties.getProperty("mail.smtp.user");
		final String password = properties.getProperty("mail.smtp.password");
		properties.put("mail.smtp.auth", "true");
		Session session = Session.getInstance(properties,
				new javax.mail.Authenticator() {
					protected PasswordAuthentication getPasswordAuthentication() {
						return new PasswordAuthentication(user, password);
					}
				});

		MimeMessage message = new MimeMessage(session);
		message.setFrom(new InternetAddress(from));
		message.addRecipient(javax.mail.Message.RecipientType.TO,
				new InternetAddress(to));
		message.setSubject("Signup Notification");
		BodyPart messageBodyPart = new MimeBodyPart();
		messageBodyPart.setContent("Follow this link to access your account and update your password in the User Settings. This direct link will self-destruct once used. \n http://admin." + Simple.getBaseDomain() + ":" + Simple.getPort() + "?nonce=" + SecurityService.get().generateNonce(u), "text/html");

		Multipart multipart = new MimeMultipart();
		multipart.addBodyPart(messageBodyPart);

		// Send the complete message parts
		message.setContent(multipart);

		// Send message
		Transport.send(message);
	}

